mapper.update:
UPDATE LOCATION
SET 
	 area = #{area}
	,type = #{type}
	,name = #{name}
	,address = #{address}
	,description = #{description}
	,update_date = now()
WHERE 
	 idx = #{location_idx}

mapper.select:
SELECT COUNT(*)
FROM LOCATION



		AND type=#{searchType}


		AND area=#{searchArea}


		AND name=#{searchName}

SELECT idx AS location_idx, name, DATE_FORMAT(create_date, '%Y-%m-%d') AS create_date, CASE WHEN area = 'SEOUL' THEN '서울' WHEN area = 'GANGNEUNG' THEN '강릉' WHEN area = 'YEOSU' THEN '여수' WHEN area = 'BUSAN' THEN '부산' WHEN area = 'JEJU' THEN '제주' END AS area, CASE WHEN type = 'A' THEN '관광지' WHEN type = 'R' THEN '맛집' END AS type
FROM LOCATION
AND type=#{searchType}
AND area=#{searchArea}
AND name=#{searchName}
ORDER BY create_date DESC

SELECT *
FROM KEYWORD k JOIN LOCATION_KEYWORD lk ON (k.idx = lk.keyword_idx)
WHERE lk.location_idx = #{location_idx}
