mapper.update:
UPDATE NOTICE
SET title = #{title}, content = #{content}, status = #{status}
WHERE notice_idx = #{notice_idx}

mapper.select:
SELECT
FROM NOTICE WHERE status = 'Y' ORDER BY create_date DESC
LIMIT #{offset}, #{limit}

SELECT
FROM NOTICE WHERE 1=1
AND title LIKE CONCAT('%', #{keyword}, '%')
AND status = #{status}
AND target = #{target}
ORDER BY create_date DESC LIMIT #{offset}, #{limit}
