mapper.update:
UPDATE PARTNER
SET
	  address = #{address}
	, description = #{description}
	, check_in = #{check_in}
	, check_out = #{check_out} 
	, type = #{type} 
	, area = #{area} 
	, info_yn = 'Y'
	, update_date = now()

	, address_si = #{address_si}


	, address_dong = #{address_dong}


	, latitude = #{latitude} 


	, hardness = #{hardness} 

WHERE 
	idx = #{partner_idx}

mapper.insert:
INSERT INTO PARTNER_FACILITIES (
	  partner_idx
	, facilities_idx
) VALUES 

(
	  #{partner_idx}
	, #{facilities_idx}
)

INSERT INTO IMAGE (
	  mapping_idx
	, url
	, origin_filename
	, thumbnail_yn
	, type
) VALUES (
	  #{partner_idx}
	, #{url}
	, #{origin_filename}
	, #{thumbnail_yn}
	, 'P'
)

INSERT INTO PARTNER_KEYWORD (
	  partner_idx
	, keyword_idx
) VALUES 

(
	  #{partner_idx}
	, #{keyword_idx}
)
