mapper.select:
	SELECT COUNT(*)
    FROM PARTNER
    WHERE email = #{email}

mapper.update:
UPDATE PARTNER
SET 
	  business_phone = #{phone}
	, business_name = #{name}
WHERE idx = #{partnerIdx}

mapper.select:
SELECT
    idx AS partner_idx
FROM PARTNER
WHERE idx = #{partnerIdx}
  AND password = #{password}

mapper.update:
UPDATE PARTNER
SET account_bank = #{account_bank},
    account_number = #{account_number}
WHERE idx = #{partnerIdx}
