mapper.select:
SELECT COUNT(*)
FROM PARTNER p JOIN RESERVATION r ON(p.idx = r.partner_idx)   
WHERE r.user_idx = #{user_idx}

SELECT r.idx AS reservation_idx, p.idx AS partner_idx, p.name, p.address, r.check_in_date, r.check_out_date, r.guest_cnt, r.price, CASE WHEN r.check_in_date >= CURDATE() THEN 'Y' ELSE 'N' END AS check_date, r.payment_status, (SELECT idx FROM REVIEW WHERE reservation_idx = r.idx) AS review FROM PARTNER p JOIN RESERVATION r ON(p.idx = r.partner_idx) WHERE r.user_idx = #{user_idx}
ORDER BY r.reservation_date DESC
LIMIT ${limit} OFFSET ${offset}

SELECT rm.room_type
FROM RESERVATION rs JOIN ROOM rm ON(rs.room_idx = rm.idx)
WHERE rs.idx = #{reservation_idx}
