mapper.sql:
WHERE approval_status = 'Y'
AND AREA = #{area}
AND p.type IN
#{type}
AND p.idx IN ( SELECT partner_idx FROM PARTNER_FACILITIES WHERE facilities_idx IN
#{facilities}
)
AND p.idx IN ( SELECT r.partner_idx FROM ROOM_FACILITIES rf JOIN ROOM r ON r.idx = rf.room_idx WHERE facilities_idx IN
#{facilities}
)
AND p.idx IN ( SELECT partner_idx FROM PARTNER_KEYWORD WHERE keyword_idx IN
#{keyword}
)
AND p.idx IN ( SELECT partner_idx FROM ROOM WHERE (CASE WHEN WEEKDAY(#{start_date}) IN (0, 1, 2, 3, 4) THEN basic_price WHEN WEEKDAY(#{start_date}) IN (5, 6) THEN weekend_price END) BETWEEN #{min_price} AND #{max_price} )
AND p.idx IN ( SELECT partner_idx FROM ROOM WHERE maximum >= #{guest} )
