mapper.insert:
INSERT INTO REVIEW (
	  user_idx
	, reservation_idx
	, content
	, score_1
	, score_2
	, score_3
	, score_4
	, score_avg
) VALUES (
	  #{user_idx}
	, #{reservation_idx}
	, #{content}
	, #{rating1}
	, #{rating2}
	, #{rating3}
	, #{rating4}
	, (#{rating1} + #{rating2} + #{rating3} + #{rating4}) / 4
)

INSERT INTO IMAGE (
	  mapping_idx
	, type
	, url
	, origin_filename
	, thumbnail_yn
) VALUES (
	  #{review_idx}
	, 'V'
	, #{url}
	, #{origin_filename}
	, #{thumbnail_yn}
)
